import React, { useState, useEffect } from 'react';
import './styles/global.css'
import './styles/App.css'
import './styles/SideBar.css'
import './styles/Main.css'

import api from './services/api.js'
import DevItem from './components/DevItems/'

//Componente => bloco isolado de HTML, CSS e JS, o qual não interfere no restante da explicação
//Estado => informação que o componente vai mudar, mantidas pelo componente(lembrar: imutabilidade)
//Propriedade => informações que um componente(Parent) passa para os componentes(Children)

//const key = "KEY1_KEY1_KFJAKFJAL"

function App(){
  const [github_username, setGithubUsername] = useState('')
  const [techs, setTechs] = useState('')

  const [latitude, setLatitude] = useState('')
  const [longitude, setLongitude] = useState('')

  const [devs, setDevs] = useState([])

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords
        setLatitude(latitude)
        setLongitude(longitude)
      },
      (err) => {
        console.log(err)
      },
      {
        timeout: 30000
      }
    )
  }, [])

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs')
      setDevs(response.data)
    }

    loadDevs()
  }, [])

  async function handleAddDev(e) {
    e.preventDefault()

    const response = await api.post('/devs', {
      github_username,
      techs,
      latitude,
      longitude
    })

    //console.log(response.data)
    setGithubUsername('')
    setTechs('')

    setDevs(elements => [...elements, response.data] )
  }

  return (
    <div id="app">
      <aside>
        <h1><strong>Register</strong></h1>
        <form>
          <div className="input-block">
            <label htmlFor="github_username">Github Username</label>
            <input 
              name="github_username" 
              id="github_username" 
              value={github_username}
              onChange={e => setGithubUsername(e.target.value)}
              required
            />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Technologies: </label>
            <input 
              name="techs"
              id="techs" 
              value={techs}
              onChange={e => setTechs(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude">Latitude: </label>
              <input 
                type="number" 
                name="latitude" 
                id="latitude" 
                value={latitude} 
                onChange={e => setLatitude(e.target.value)}
                required
              />
            </div>   

            <div className="input-block">
               <label htmlFor="longitude">Longitude: </label>
              <input 
                type="number"
                name="longitude"
                id="longitude" 
                value={longitude}
                onChange={e => setLongitude(e.target.value)}
                required
              />
            </div>          
          </div>


          <button type="submit" onClick={handleAddDev}>Submit</button>
        </form>
      </aside>
      <main>
        <ul>
          {devs.map(dev => ( 
            <DevItem key={dev._id} dev={dev}/>
          ))}
        </ul>
      </main>
    </div>
  )
}

export default App;
